 <?php     /*start session*/	session_start();	include 'config.php';    /*variable declaration*/		$type="";	$messErr=$passwordErr=$emailErr="";	if( $_SERVER["REQUEST_METHOD"] == "POST")	{						include('validation/valid_check.php');			include('func_query/query.php');			$email = $_POST['email'];			$pass=$_POST['pass'];			/*email and password check*/			$emailErr=blank_Email($email);			$passwordErr=blank_Password($pass); 			$pass=md5($pass);			/*Retrieve email and password from database for log in*/			$result= login_retrieve($db,$email,$pass);			$count = mysqli_num_rows($result);			if($count ==1 )			{								while($row = mysqli_fetch_array($result))				{					$uid=$row['st_id'];					$email=$row['email'];					include('encrypt_decrypt/en_de_crypt.php');					$id=encrypt($uid);					$token=mt_rand();                    /*check loged id is a user or admin */					if($row['type']==2)					{					    $_SESSION['st_id']=$id;						$_SESSION['Token']=$token;						login_update($db,$token,$email,$pass);						header('location:adminprofile.php');					}					else					{						$_SESSION['st_id']=$id;						$_SESSION['Token']=$token;						login_update($db,$token,$email,$pass);						header('location:userprofile.php');					}				}			}			else			{				throw new Exception('Invalid Username or Password');			}					}	?><?php include('header/header.php');?>	<div class="wrapper">		<div class="container">			<div class="sign_main col-md-offset-3 col-md-6">				<div class="col-md-12 col-sm-12  reg_header text-center">					<h2>User Log In</h2>					<div class="col-md-3 col-md-offset-9">						<a href="index.php" class="btn btn-success">Sign Up</a>					</div>									</div>                <!--Email Form-->				<form class = "form-horizontal"  name="loginform" method="post" action="signin.php" >  					<div class="form-group text-center">						<div class="col-md-6 col-md-offset-3">							<span class="error"><?php echo  $emailErr;?></span>							<span id="email"></span>							<input type = "text" class = "form-control col-md-6" placeholder="enter your email id" name="email">						</div>					</div>					<!--password Form-->					<div class="form-group text-center">						<div class="col-md-6 col-md-offset-3">						<span class="error"><?php echo  $passwordErr;?></span>							<input type = "password" class = "form-control" placeholder="enter your account password" name="pass">													</div>					</div>					<div class="form-group" > 						<div class="lgbtn  text-center ">						     <span class="error"><?php echo  $messErr;?></span>							<input class="btn btn-primary BUTTON col-md-3  col-md-offset-5" type="submit" name="submit" value="Log In">							<a href="">Forget Password</a><br>						</div>											</div>				</form>			</div>		</div>	</div></body></html>